section.script:
         //funktioniert wegen Cross-Origin-Requests nur auf dem eigenen Server
        $.getJSON('../api/v1/search?q=objectType:mods&rows=4&sort=modified desc&wt=json',
            function (data) {
              $('#doc_count').text(data.response.numFound);

              $(data.response.docs).each(function(i, doc){
                let author = doc['mods.nameByRole.personal.aut'] ? doc['mods.nameByRole.personal.aut'] : ' ';
                $('#last4').append($('
')
                    .append($('').append($('
')
                      .append($('')
                        .append('
'+doc['mods.dateIssued']+'',
                          ''+doc['search_result_link_text']+'',
                          '
'+author[0].split(":")[0]+'',
                          '
'+doc['modifiedby']+', am ' +new Date(doc['modified']).toLocaleString()+''
                      )))));
            });
        });
